import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReportsPage.css";

const API_BASE_URL = "http://localhost:3000/api/reports";

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReports(res.data);
    } catch (err) {
      setError(
        err.response?.data?.message || err.message || "Failed to fetch reports"
      );
    } finally {
      setLoading(false);
    }
  };

  // Add Salary Adjustment CSV download button
  const handleDownloadSalaryAdjustmentCSV = async () => {
    const token = localStorage.getItem("token");
    try {
      const adjRes = await axios.get("http://localhost:3000/api/adjustments", {
        headers: { Authorization: `Bearer ${token}` }
      });
      const adjustments = Array.isArray(adjRes.data) ? adjRes.data : [];
      const headers = ["AdjustmentID","UserID","AdjustmentType","Amount","MonthYear","Remarks","CreatedAt"];
      const csvRows = [headers.join(",")];
      adjustments.forEach(a => {
        csvRows.push([
          a.AdjustmentID || '',
          a.UserID || '',
          a.AdjustmentType || '',
          a.Amount || '',
          a.MonthYear || '',
          a.Remarks || '',
          a.CreatedAt || ''
        ].join(","));
      });
      const csvContent = csvRows.join("\n");
      const blob = new Blob([csvContent], { type: "text/csv" });
      const blobUrl = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = blobUrl;
      link.setAttribute("download", "salary_adjustments.csv");
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      alert("Download failed: " + (err.response?.data?.message || err.message));
    }
  };

  return (
    <div className="reports-container">
      <h2 className="reports-title">Available Reports</h2>
      <button className="reports-download-btn" style={{marginBottom:16}} onClick={handleDownloadSalaryAdjustmentCSV}>
        Download Salary Adjustments CSV
      </button>
      {loading ? (
        <div className="reports-loading">Loading...</div>
      ) : error ? (
        <div className="reports-error">{error}</div>
      ) : (
        <table className="reports-table">
          <thead>
            <tr>
              <th>Report ID</th>
              <th>Type</th>
              <th>Description</th>
              <th>Download</th>
            </tr>
          </thead>
          <tbody>
            {reports.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", color: "#888" }}>
                  No reports found.
                </td>
              </tr>
            ) : (
              reports
                .filter(
                  (report) =>
                    report.id !== "payslip" &&
                    report.id !== "compliance" &&
                    report.id !== "payroll_summary"
                )
                .map((report) => (
                  <tr key={report.id}>
                    <td>{report.id}</td>
                    <td>{report.name}</td>
                    <td>{report.description}</td>
                    <td>
                      {report.id === "leaves_csv" ? (
                        <button
                          className="reports-download-btn"
                          onClick={async () => {
                            const token = localStorage.getItem("token");
                            try {
                              // Fetch leave data from leave API
                              const leaveRes = await axios.get("http://localhost:3000/api/leaves", {
                                headers: { Authorization: `Bearer ${token}` }
                              });
                              const leaves = leaveRes.data;
                              // Convert to CSV
                              const headers = ["LeaveID","UserID","LeaveType","LeaveDays","MonthYear","CreatedAt"];
                              const csvRows = [headers.join(",")];
                              leaves.forEach(l => {
                                // Ensure order matches headers
                                csvRows.push([
                                  l.LeaveID || '',
                                  l.UserID || '',
                                  l.LeaveType || '',
                                  l.LeaveDays || '',
                                  l.MonthYear || '',
                                  l.CreatedAt || ''
                                ].join(","));
                              });
                              const csvContent = csvRows.join("\n");
                              const blob = new Blob([csvContent], { type: "text/csv" });
                              const blobUrl = window.URL.createObjectURL(blob);
                              const link = document.createElement("a");
                              link.href = blobUrl;
                              link.setAttribute("download", "leaves.csv");
                              document.body.appendChild(link);
                              link.click();
                              link.remove();
                            } catch (err) {
                              alert("Download failed: " + (err.response?.data?.message || err.message));
                            }
                          }}
                        >
                          Download
                        </button>
                      ) : report.endpoint ? (
                        <button
                          className="reports-download-btn"
                          onClick={async () => {
                            const token = localStorage.getItem("token");
                            try {
                              const url =
                                report.id === "compliance_csv"
                                  ? `http://localhost:3000${report.endpoint}?month=Sep-2025`
                                  : `http://localhost:3000${report.endpoint}`;
                              const response = await axios.get(url, {
                                headers: { Authorization: `Bearer ${token}` },
                                responseType: "blob",
                              });
                              const fileName =
                                report.id === "compliance_csv"
                                  ? `compliance_Sep-2025.csv`
                                  : `${report.id}.csv`;
                              const blobUrl = window.URL.createObjectURL(
                                new Blob([response.data])
                              );
                              const link = document.createElement("a");
                              link.href = blobUrl;
                              link.setAttribute("download", fileName);
                              document.body.appendChild(link);
                              link.click();
                              link.remove();
                            } catch (err) {
                              alert(
                                "Download failed: " +
                                  (err.response?.data?.message || err.message)
                              );
                            }
                          }}
                        >
                          Download
                        </button>
                      ) : (
                        "-"
                      )}
                    </td>
                  </tr>
                ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ReportsPage;
