import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AuthPage.css';

const API_BASE_URL = 'http://localhost:3000/api/auth';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'Employee',
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post(`${API_BASE_URL}/login`, {
          email: formData.email,
          password: formData.password,
        });
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('user', JSON.stringify(res.data.user));
        const user = res.data.user;
        alert(`Login successful. Welcome ${user ? user.name : 'User'}`);
        navigate('/payroll');
      } else {
        await axios.post(`${API_BASE_URL}/register`, formData);
        alert('Registration successful. You can now login.');
        setIsLogin(true);
      }
    } catch (err) {
      let errorMsg = 'Something went wrong';
      if (err.response) {
        errorMsg = err.response.data?.message || JSON.stringify(err.response.data) || err.message;
      } else if (err.request) {
        errorMsg = 'No response from server. Is backend running?';
      } else {
        errorMsg = err.message;
      }
      alert('Error: ' + errorMsg);
      console.error('Auth error:', err);
    }
  };

  return (
    <div className="auth-container">
      <h1 style={{ textAlign: 'center', fontWeight: 'bold', marginBottom: '8px' }}>Payroll maven</h1>
      <h2 className="auth-title">{isLogin ? 'Login' : 'Register'}</h2>
      <form className="auth-form" onSubmit={handleSubmit}>
        {!isLogin && (
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleChange}
            required
            className="auth-input"
          />
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
          className="auth-input"
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
          className="auth-input"
        />
        {!isLogin && (
          <select name="role" value={formData.role} onChange={handleChange} className="auth-select">
            <option value="Admin">Admin</option>
            <option value="HR">HR</option>
            <option value="Employee">Employee</option>
          </select>
        )}
        <button type="submit" className="auth-button">
          {isLogin ? 'Login' : 'Register'}
        </button>
      </form>
      <div style={{ textAlign: 'center' }}>
        {isLogin ? "Don't have an account?" : 'Already have an account?'}{' '}
        <button type="button" onClick={() => setIsLogin(!isLogin)} className="auth-switch">
          {isLogin ? 'Register' : 'Login'}
        </button>
      </div>
    </div>
  );
};

export default AuthPage;
