// PayrollRunPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './PayrollRunPage.css';

const API_BASE_URL = 'http://localhost:3000/api/payroll-runs';


const PayrollRunPage = () => {
  const [runs, setRuns] = useState([]);
  const [form, setForm] = useState({
    UserID: '',
    MonthYear: '',
  });
  const [loading, setLoading] = useState(false);
  const [preview, setPreview] = useState(null);
  const [previewLoading, setPreviewLoading] = useState(false);

  useEffect(() => {
    fetchRuns();
  }, []);

  const fetchRuns = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRuns(res.data);
    } catch (err) {
      alert('Failed to fetch payroll runs');
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Frontend validation
    const userIdNum = Number(form.UserID);
    const monthYearRegex = /^[A-Za-z]{3}-\d{4}$/;
    if (!userIdNum || userIdNum < 1) {
      alert('Please enter a valid numeric UserID.');
      return;
    }
    if (!monthYearRegex.test(form.MonthYear)) {
      alert('Please enter MonthYear in format e.g. Sep-2025.');
      return;
    }
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(API_BASE_URL, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setForm({ UserID: '', MonthYear: '' });
      // Instead of refetching all, add preview data as new row
      if (preview) {
        setRuns(prev => [
          {
            PayrollRunID: 'Preview',
            UserID: form.UserID,
            MonthYear: form.MonthYear,
            GrossSalary: preview.grossSalary,
            NetSalary: preview.netSalary,
            PF: preview.PF,
            ESI: preview.ESI,
            TDS: preview.TDS,
            SalaryAdjustment: (preview.bonus ? `+${preview.bonus}` : '') + (preview.deduction ? ` -${preview.deduction}` : ''),
            // Optionally add more preview fields if needed
          },
          ...prev
        ]);
      } else {
        fetchRuns();
      }
      alert('Payroll run inserted successfully!');
    } catch (err) {
      alert(
        err?.response?.data?.message ||
        err?.response?.data?.error ||
        err?.message ||
        'Failed to insert payroll run'
      );
    }
    setLoading(false);
  };

  // Preview handler
  const handlePreview = async (e) => {
    e.preventDefault();
    const userIdNum = Number(form.UserID);
    const monthYearRegex = /^[A-Za-z]{3}-\d{4}$/;
    if (!userIdNum || userIdNum < 1) {
      alert('Please enter a valid numeric UserID.');
      return;
    }
    if (!monthYearRegex.test(form.MonthYear)) {
      alert('Please enter MonthYear in format e.g. Sep-2025.');
      return;
    }
    setPreviewLoading(true);
    setPreview(null);
    try {
      const token = localStorage.getItem('token');
      // Fetch payroll base
      const baseRes = await axios.get(`http://localhost:3000/api/payroll-base/${form.UserID}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const base = baseRes.data;
      // Fetch leaves
      const leaveRes = await axios.get(`http://localhost:3000/api/leaves/${form.UserID}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const leaves = Array.isArray(leaveRes.data) ? leaveRes.data.filter(l => l.MonthYear === form.MonthYear) : [];
      const lop = leaves.filter(l => l.LeaveType === 'LOP').reduce((sum, l) => sum + Number(l.LeaveDays), 0);
      // Fetch salary adjustments
      const adjRes = await axios.get(`http://localhost:3000/api/adjustments/${form.UserID}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const adjustments = Array.isArray(adjRes.data) ? adjRes.data.filter(a => a.MonthYear === form.MonthYear) : [];
      const bonus = adjustments.filter(a => a.AdjustmentType === 'Bonus').reduce((sum, a) => sum + Number(a.Amount), 0);
      const deduction = adjustments.filter(a => a.AdjustmentType === 'Deduction').reduce((sum, a) => sum + Number(a.Amount), 0);
      // Calculate gross salary
      let grossSalary = Number(base.BasicSalary || 0) + Number(base.HRA || 0) + Number(base.DA || 0) + Number(base.OtherAllowance || 0);
      const lopDeduction = +(lop * (grossSalary / 30)).toFixed(2);
      grossSalary -= lopDeduction;
      const adjustedGross = grossSalary + bonus - deduction;
      const PF = +(adjustedGross * 0.12).toFixed(2);
      const ESI = +(adjustedGross * 0.0075).toFixed(2);
      const TDS = +(adjustedGross * 0.1).toFixed(2);
      const netSalary = +(adjustedGross - PF - ESI - TDS).toFixed(2);
      setPreview({
        base,
        leaves,
        adjustments,
        grossSalary,
        lop,
        lopDeduction,
        bonus,
        deduction,
        adjustedGross,
        PF,
        ESI,
        TDS,
        netSalary
      });
    } catch (err) {
      alert('Preview fetch failed: ' + (err?.response?.data?.message || err?.message));
    }
    setPreviewLoading(false);
  };

  return (
    <div className="payrollrun-container">
      <h2 className="payrollrun-title">Payroll Run Records</h2>
      {/* Payroll Run Insert Form */}
      <form className="payrollrun-form" onSubmit={handleSubmit} style={{ marginBottom: 24, display: 'flex', gap: 16, alignItems: 'center', flexWrap: 'wrap' }}>
        <input
          name="UserID"
          value={form.UserID}
          onChange={handleChange}
          placeholder="UserID"
          required
          className="payrollrun-input"
        />
        <input
          name="MonthYear"
          value={form.MonthYear}
          onChange={handleChange}
          placeholder="MonthYear (e.g. Sep-2025)"
          required
          className="payrollrun-input"
        />
        <button className="payrollrun-btn" type="submit" disabled={loading}>
          {loading ? 'Submitting...' : 'Insert Payroll Run'}
        </button>
        <button className="payrollrun-btn" type="button" style={{ background: '#e0e7ff', color: '#2563eb' }} onClick={handlePreview} disabled={previewLoading}>
          {previewLoading ? 'Loading...' : 'Preview'}
        </button>
      </form>
      {/* Preview Section */}
      {preview && (
        <div style={{ background: '#f3f4f6', borderRadius: 8, padding: 16, marginBottom: 24 }}>
          <h3 style={{ marginTop: 0 }}>Payroll Preview</h3>
          <div><b>Gross Salary:</b> ₹{preview.grossSalary}</div>
          <div><b>LOP Days:</b> {preview.lop} <b>LOP Deduction:</b> ₹{preview.lopDeduction}</div>
          <div><b>Bonus:</b> ₹{preview.bonus} <b>Deduction:</b> ₹{preview.deduction}</div>
          <div><b>Adjusted Gross:</b> ₹{preview.adjustedGross}</div>
          <div><b>PF:</b> ₹{preview.PF} <b>ESI:</b> ₹{preview.ESI} <b>TDS:</b> ₹{preview.TDS}</div>
          <div><b>Net Salary:</b> ₹{preview.netSalary}</div>
          <hr style={{margin:'16px 0'}} />
          <div>
            <b>Payroll Base:</b>
            <pre style={{background:'#fff',padding:'8px',borderRadius:'6px',overflowX:'auto'}}>{JSON.stringify(preview.base, null, 2)}</pre>
          </div>
          <div>
            <b>Leave Records (for month):</b>
            <pre style={{background:'#fff',padding:'8px',borderRadius:'6px',overflowX:'auto'}}>{JSON.stringify(preview.leaves, null, 2)}</pre>
          </div>
          <div>
            <b>Salary Adjustments (for month):</b>
            <pre style={{background:'#fff',padding:'8px',borderRadius:'6px',overflowX:'auto'}}>{JSON.stringify(preview.adjustments, null, 2)}</pre>
          </div>
        </div>
      )}
      <div className="payrollrun-table-wrapper">
        <table className="payrollrun-table">
          <thead>
            <tr>
              <th style={{ textAlign: 'center' }}>PayrollRunID</th>
              <th style={{ textAlign: 'center' }}>UserID</th>
              <th style={{ textAlign: 'center' }}>Month/Year</th>
              <th style={{ textAlign: 'center' }}>Gross Salary</th>
              <th style={{ textAlign: 'center' }}>Net Salary</th>
              <th style={{ textAlign: 'center' }}>PF</th>
              <th style={{ textAlign: 'center' }}>ESI</th>
              <th style={{ textAlign: 'center' }}>TDS</th>
              <th style={{ textAlign: 'center' }}>Salary Adjustment</th>
            </tr>
          </thead>
          <tbody>
            {runs.length === 0 ? (
              <tr><td colSpan={9} style={{ textAlign: 'center' }}>No payroll runs found.</td></tr>
            ) : (
              runs.map((run) => (
                <tr key={run.PayrollRunID}>
                  <td style={{ textAlign: 'center' }}>{run.PayrollRunID}</td>
                  <td style={{ textAlign: 'center' }}>{run.UserID}</td>
                  <td style={{ textAlign: 'center' }}>{run.MonthYear}</td>
                  <td style={{ textAlign: 'center' }}>{run.GrossSalary}</td>
                  <td style={{ textAlign: 'center' }}>{run.NetSalary}</td>
                  <td style={{ textAlign: 'center' }}>{run.PF}</td>
                  <td style={{ textAlign: 'center' }}>{run.ESI}</td>
                  <td style={{ textAlign: 'center' }}>{run.TDS}</td>
                  <td style={{ textAlign: 'center' }}>{run.SalaryAdjustment
                    ? run.SalaryAdjustment.replace(/\+0*([1-9]\d*|0)(\.\d+)?/g, '+$1$2').replace(/-0*([1-9]\d*|0)(\.\d+)?/g, '-$1$2')
                    : ''}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PayrollRunPage;
