import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReportsPage.css";

const API_BASE_URL = "http://localhost:3000/api/reports";

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReports(res.data);
    } catch (err) {
      setError(
        err.response?.data?.message || err.message || "Failed to fetch reports"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reports-container">
      <h2 className="reports-title">Available Reports</h2>
      {loading ? (
        <div className="reports-loading">Loading...</div>
      ) : error ? (
        <div className="reports-error">{error}</div>
      ) : (
        <table className="reports-table">
          <thead>
            <tr>
              <th>Report ID</th>
              <th>Type</th>
              <th>Description</th>
              <th>Download</th>
            </tr>
          </thead>
          <tbody>
            {reports.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", color: "#888" }}>
                  No reports found.
                </td>
              </tr>
            ) : (
              reports
                .filter(
                  (report) =>
                    report.id !== "payslip" &&
                    report.id !== "compliance" &&
                    report.id !== "payroll_summary"
                )
                .map((report) => (
                  <tr key={report.id}>
                    <td>{report.id}</td>
                    <td>{report.name}</td>
                    <td>{report.description}</td>
                    <td>
                      {report.id === "leaves_csv" ? (
                        <button
                          className="reports-download-btn"
                          onClick={async () => {
                            const token = localStorage.getItem("token");
                            try {
                              // Fetch leave data from leave API
                              const leaveRes = await axios.get("http://localhost:3000/api/leaves", {
                                headers: { Authorization: `Bearer ${token}` }
                              });
                              const leaves = leaveRes.data;
                              // Format CSV using papaparse
                              const Papa = require('papaparse');
                              const headers = ["LeaveID","UserID","LeaveType","LeaveDays","MonthYear","CreatedAt"];
                              const csvContent = Papa.unparse(leaves, {
                                quotes: true,
                                header: true,
                                columns: headers
                              });
                              const blob = new Blob([csvContent], { type: "text/csv" });
                              const blobUrl = window.URL.createObjectURL(blob);
                              const link = document.createElement("a");
                              link.href = blobUrl;
                              link.setAttribute("download", "leaves.csv");
                              document.body.appendChild(link);
                              link.click();
                              link.remove();
                            } catch (err) {
                              alert("Download failed: " + (err.response?.data?.message || err.message));
                            }
                          }}
                        >
                          Download
                        </button>
                      ) : report.id === "salary_adjustment_csv" ? (
                        <button
                          className="reports-download-btn"
                          onClick={async () => {
                            const token = localStorage.getItem("token");
                            try {
                              // Fetch salary adjustment data from API
                              const res = await axios.get("http://localhost:3000/api/salary-adjustments", {
                                headers: { Authorization: `Bearer ${token}` }
                              });
                              // Check if backend returned error message instead of data
                              if (res.data && typeof res.data === 'object' && res.data.message) {
                                alert("Download failed: " + res.data.message);
                                return;
                              }
                              const adjustments = Array.isArray(res.data) ? res.data : [];
                              if (adjustments.length === 0) {
                                alert("No salary adjustment data available.");
                                return;
                              }
                              // Format CSV using papaparse
                              const Papa = require('papaparse');
                              const headers = ["AdjustmentID","UserID","Amount","Reason","MonthYear","CreatedAt"];
                              const formatted = adjustments.map(a => ({
                                AdjustmentID: a.AdjustmentID || '',
                                UserID: a.UserID || '',
                                Amount: a.Amount || '',
                                Reason: a.Reason || '',
                                MonthYear: a.MonthYear || '',
                                CreatedAt: a.CreatedAt || ''
                              }));
                              const csvContent = Papa.unparse(formatted, {
                                quotes: true,
                                header: true,
                                columns: headers
                              });
                              const blob = new Blob([csvContent], { type: "text/csv" });
                              const blobUrl = window.URL.createObjectURL(blob);
                              const link = document.createElement("a");
                              link.href = blobUrl;
                              link.setAttribute("download", "salary_adjustments.csv");
                              document.body.appendChild(link);
                              link.click();
                              link.remove();
                            } catch (err) {
                              alert("Download failed: " + (err.response?.data?.message || err.message));
                            }
                          }}
                        >
                          Download
                        </button>
                      ) : report.endpoint ? (
                        <button
                          className="reports-download-btn"
                          onClick={async () => {
                            const token = localStorage.getItem("token");
                            try {
                              const url =
                                report.id === "compliance_csv"
                                  ? `http://localhost:3000${report.endpoint}?month=Sep-2025`
                                  : `http://localhost:3000${report.endpoint}`;
                              const response = await axios.get(url, {
                                headers: { Authorization: `Bearer ${token}` },
                                responseType: "blob",
                              });
                              const fileName =
                                report.id === "compliance_csv"
                                  ? `compliance_Sep-2025.csv`
                                  : `${report.id}.csv`;
                              const blobUrl = window.URL.createObjectURL(
                                new Blob([response.data])
                              );
                              const link = document.createElement("a");
                              link.href = blobUrl;
                              link.setAttribute("download", fileName);
                              document.body.appendChild(link);
                              link.click();
                              link.remove();
                            } catch (err) {
                              alert(
                                "Download failed: " +
                                  (err.response?.data?.message || err.message)
                              );
                            }
                          }}
                        >
                          Download
                        </button>
                      ) : (
                        "-"
                      )}
                    </td>
                  </tr>
                ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ReportsPage;
