import React, { useEffect, useState } from "react";
// PayslipDownloadTable: fetches payslip report and shows download buttons for each slip
function PayslipDownloadTable() {
  const [payslips, setPayslips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchPayslips();
    // eslint-disable-next-line
  }, []);

  const fetchPayslips = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:3000/api/reports/payslips", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPayslips(res.data);
    } catch (err) {
      setError(err.response?.data?.message || err.message || "Failed to fetch payslips");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <span>Loading payslips...</span>;
  if (error) return <span style={{ color: "#e53e3e" }}>{error}</span>;
  if (!payslips.length) return <span>No payslips found.</span>;

  return (
    <div style={{ minWidth: 300 }}>
      <table style={{ width: "100%", background: "#f9f9f9", borderRadius: 4 }}>
        <thead>
          <tr>
            <th>Payslip ID</th>
            <th>User ID</th>
            <th>Month/Year</th>
            <th>Net Salary</th>
            <th>Download</th>
          </tr>
        </thead>
        <tbody>
          {payslips.map((slip) => (
            <tr key={slip.PayslipID}>
              <td>{slip.PayslipID}</td>
              <td>{slip.UserID}</td>
              <td>{slip.MonthYear}</td>
              <td>{slip.NetSalary}</td>
              <td>
                <button
                  className="reports-download-btn"
                  onClick={async () => {
                    const token = localStorage.getItem("token");
                    try {
                      const response = await axios.get(
                        `http://localhost:3000${slip.downloadUrl}`,
                        {
                          headers: { Authorization: `Bearer ${token}` },
                          responseType: "blob",
                        }
                      );
                      const url = window.URL.createObjectURL(new Blob([response.data]));
                      const link = document.createElement("a");
                      link.href = url;
                      link.setAttribute("download", `payslip_${slip.PayslipID}.pdf`);
                      document.body.appendChild(link);
                      link.click();
                      link.remove();
                    } catch (err) {
                      alert("Download failed: " + (err.response?.data?.message || err.message));
                    }
                  }}
                >
                  Download
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
import axios from "axios";
import "./ReportsPage.css";

const API_BASE_URL = "http://localhost:3000/api/reports";

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReports(res.data);
    } catch (err) {
      setError(
        err.response?.data?.message || err.message || "Failed to fetch reports"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reports-container">
      <h2 className="reports-title">Available Reports</h2>
      {loading ? (
        <div className="reports-loading">Loading...</div>
      ) : error ? (
        <div className="reports-error">{error}</div>
      ) : (
        <table className="reports-table">
          <thead>
            <tr>
              <th>Report ID</th>
              <th>Type</th>
              <th>Description</th>
              <th>Endpoint</th>
              <th>Download</th>
            </tr>
          </thead>
          <tbody>
            {reports.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", color: "#888" }}>
                  No reports found.
                </td>
              </tr>
            ) : (
              reports.map((report) => (
                <tr key={report.id}>
                  <td>{report.id}</td>
                  <td>{report.name}</td>
                  <td>{report.description}</td>
                  <td>{report.endpoint}</td>
                  <td>
                    {report.id === "compliance_csv" ? (
                      <button
                        className="reports-download-btn"
                        onClick={async () => {
                          const token = localStorage.getItem("token");
                          try {
                            const response = await axios.get(
                              `http://localhost:3000${report.endpoint}?month=Sep-2025`,
                              {
                                headers: { Authorization: `Bearer ${token}` },
                                responseType: "blob",
                              }
                            );
                            const url = window.URL.createObjectURL(new Blob([response.data]));
                            const link = document.createElement("a");
                            link.href = url;
                            link.setAttribute("download", `compliance_Sep-2025.csv`);
                            document.body.appendChild(link);
                            link.click();
                            link.remove();
                          } catch (err) {
                            alert("Download failed: " + (err.response?.data?.message || err.message));
                          }
                        }}
                      >
                        Download
                      </button>
                    ) : report.id === "payslip" ? (
                      <PayslipDownloadTable />
                    ) : (
                      "-"
                    )}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ReportsPage;
