// This file contains the PayslipsPage component to show payslips and allow download.
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./PayslipsPage.css";

const API_BASE_URL = "http://localhost:3000/api/payslips/my";

const PayslipsPage = () => {
	const [payslips, setPayslips] = useState([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState("");

	useEffect(() => {
		fetchPayslips();
	}, []);

	const fetchPayslips = async () => {
		setLoading(true);
		setError("");
		try {
			const token = localStorage.getItem("token");
			const res = await axios.get(API_BASE_URL, {
				headers: { Authorization: `Bearer ${token}` },
			});
			setPayslips(res.data);
		} catch (err) {
			setError(err.response?.data?.message || err.message || "Failed to fetch payslips");
		} finally {
			setLoading(false);
		}
	};

	const handleDownload = async (slip) => {
		const token = localStorage.getItem("token");
		try {
			const response = await axios.get(
				`http://localhost:3000${slip.downloadUrl}`,
				{
					headers: { Authorization: `Bearer ${token}` },
					responseType: "blob",
				}
			);
			// Use MonthYear and UserID for filename clarity
			const filename = `payslip_${slip.UserID}_${slip.MonthYear}.pdf`;
			const url = window.URL.createObjectURL(new Blob([response.data]));
			const link = document.createElement("a");
			link.href = url;
			link.setAttribute("download", filename);
			document.body.appendChild(link);
			link.click();
			document.body.removeChild(link);
			window.URL.revokeObjectURL(url);
		} catch (err) {
			alert("Download failed: " + (err.response?.data?.message || err.message));
		}
	};

	return (
		<div className="payslips-container">
			<h2 className="payslips-title">Payslips</h2>
			{loading ? (
				<div className="payslips-loading">Loading...</div>
			) : error ? (
				<div className="payslips-error">{error}</div>
			) : !payslips.length ? (
				<div className="payslips-empty">No payslips found.</div>
			) : (
				<table className="payslips-table">
					<thead>
						<tr>
							<th>Payslip ID</th>
							<th>User ID</th>
							<th>Month/Year</th>
							<th>Net Salary</th>
							<th>Download</th>
						</tr>
					</thead>
					<tbody>
 {payslips.map((slip) => (
 <tr key={slip.PayslipID}>
 <td>{slip.PayslipID}</td>
 <td>{slip.UserID}</td>
 <td>{slip.MonthYear}</td>
 <td>{slip.NetSalary}</td>
 <td>
 {slip.downloadUrl ? (
 <button
 className="payslips-download-btn"
 onClick={() => handleDownload(slip)}
 >
 Download
 </button>
 ) : (
 <button
 className="payslips-download-btn"
 disabled
 title="PDF not available for this payslip"
 >
 Not Available
 </button>
 )}
 </td>
 </tr>
 ))}
					</tbody>
				</table>
			)}
		</div>
	);
};

export default PayslipsPage;
