// src/pages/PayrollRunPage/PayrollRunForm.js
import React from "react";

const PayrollRunForm = ({ form, loading, previewLoading, onChange, onSubmit, onPreview }) => {
  return (
    <form className="payrollrun-form" onSubmit={onSubmit} style={{ marginBottom: 24, display: 'flex', gap: 16, alignItems: 'center', flexWrap: 'wrap' }}>
      <input
        name="UserID"
        value={form.UserID}
        onChange={onChange}
        placeholder="UserID"
        required
        className="payrollrun-input"
      />
      <input
        name="MonthYear"
        value={form.MonthYear}
        onChange={onChange}
        placeholder="MonthYear (e.g. Sep-2025)"
        required
        className="payrollrun-input"
      />
      <button className="payrollrun-btn" type="submit" disabled={loading}>
        {loading ? 'Submitting...' : 'Insert Payroll Run'}
      </button>
      <button className="payrollrun-btn" type="button" style={{ background: '#e0e7ff', color: '#2563eb' }} onClick={onPreview} disabled={previewLoading}>
        {previewLoading ? 'Loading...' : 'Preview'}
      </button>
    </form>
  );
};

export default PayrollRunForm;
