import React from "react";
import "./PayrollPage.css";

const PayrollTable = ({ payrolls, onEdit, onDelete }) => {
  return (
    <table className="payroll-table">
      <thead>
        <tr>
          <th>UserID</th>
          <th>BasicSalary</th>
          <th>HRA</th>
          <th>DA</th>
          <th>OtherAllowance</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {payrolls.length === 0 ? (
          <tr>
            <td colSpan={6} style={{ textAlign: "center" }}>
              No payroll records found.
            </td>
          </tr>
        ) : (
          payrolls.map((p) => (
            <tr key={p.UserID}>
              <td>{p.UserID}</td>
              <td>{p.BasicSalary}</td>
              <td>{p.HRA}</td>
              <td>{p.DA}</td>
              <td>{p.OtherAllowance}</td>
              <td style={{display:'flex'}}> 
                <button className="payroll-btn" onClick={() => onEdit(p)}>
                  Edit
                </button>
                <button
                  className="payroll-btn"
                  style={{ marginLeft: "8px", background: "red", color: "white" }}
                  onClick={() => onDelete(p.UserID)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))
        )}
      </tbody>
    </table>
  );
};

export default PayrollTable;
