import React from 'react';
import './Navbar.css';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  const user = JSON.parse(localStorage.getItem('user') || '{}');

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <div className="navbar-brand">
          <span className="navbar-title">Payroll Maven</span>
        </div>
      </div>
      <div className="navbar-center">
        <h1 className="navbar-heading">Payroll Management System</h1>
      </div>
      <div className="navbar-right">
        <div className="navbar-user">
          <div className="navbar-user-info">
            <span className="navbar-username">Welcome, {user.name || 'User'}</span>
            <span className="navbar-role">{user.role || 'Admin'}</span>
          </div>
        </div>
        <button className="navbar-logout" onClick={handleLogout}>
          <span>Logout</span>
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
