import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CompliancePage.css";

const API_BASE_URL = "http://localhost:3000/api/compliance";

const CompliancePage = () => {
  const [rules, setRules] = useState([]);
  const [form, setForm] = useState({
    RuleName: "",
    Description: "",
    IsActive: true,
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRules(res.data);
    } catch (err) {
      alert("Failed to fetch compliance rules");
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post(API_BASE_URL, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setForm({ RuleName: "", Description: "", IsActive: true });
      fetchRules();
    } catch (err) {
      alert("Failed to add compliance rule");
    }
  };

  const handleEdit = (rule) => {
    setEditId(rule.id || rule._id);
    setForm({
      RuleName: rule.RuleName,
      Description: rule.Description,
      IsActive: rule.IsActive,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.put(`${API_BASE_URL}/${editId}`, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEditId(null);
      setForm({ RuleName: "", Description: "", IsActive: true });
      fetchRules();
    } catch (err) {
      alert("Failed to update compliance rule");
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_BASE_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchRules();
    } catch (err) {
      alert("Failed to delete compliance rule");
    }
  };

  return (
    <div className="compliance-container">
      <h2 className="compliance-title">Compliance Rules Management</h2>
      <form className="compliance-form" onSubmit={editId ? handleUpdate : handleAdd}>
        <input
          name="RuleName"
          value={form.RuleName}
          onChange={handleChange}
          placeholder="Rule Name"
          required
          className="compliance-input"
        />
        <input
          name="Description"
          value={form.Description}
          onChange={handleChange}
          placeholder="Description"
          required
          className="compliance-input"
        />
        <label className="compliance-checkbox-label">
          <input
            type="checkbox"
            name="IsActive"
            checked={form.IsActive}
            onChange={handleChange}
          />
          Active
        </label>
        <button className="compliance-btn" type="submit">
          {editId ? "Update" : "Add"}
        </button>
        {editId && (
          <button
            className="compliance-btn"
            type="button"
            onClick={() => {
              setEditId(null);
              setForm({ RuleName: "", Description: "", IsActive: true });
            }}
          >
            Cancel
          </button>
        )}
      </form>
      <div className="compliance-table-wrapper">
        <table className="compliance-table">
          <thead>
            <tr>
              <th>Rule Name</th>
              <th>Description</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {rules.map((rule) => (
              <tr key={rule.id || rule._id}>
                <td>{rule.RuleName}</td>
                <td>{rule.Description}</td>
                <td>{rule.IsActive ? "Active" : "Inactive"}</td>
                <td>
                  <button
                    className="compliance-btn"
                    onClick={() => handleEdit(rule)}
                  >
                    Edit
                  </button>
                  <button
                    className="compliance-btn"
                    style={{ marginLeft: "8px" }}
                    onClick={() => handleDelete(rule.id || rule._id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {rules.length === 0 && (
              <tr>
                <td colSpan="4" style={{ textAlign: "center", color: "#888" }}>
                  No compliance rules found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CompliancePage;
