import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './PayrollPage.css';

const API_BASE_URL = 'http://localhost:3000/api/payroll-base';

const PayrollPage = () => {
  const [payrolls, setPayrolls] = useState([]);
  const [form, setForm] = useState({
    UserID: '',
    BasicSalary: '',
    HRA: '',
    DA: '',
    OtherAllowance: '',
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchPayrolls();
  }, []);

  const fetchPayrolls = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPayrolls(res.data);
    } catch (err) {
      let msg = 'Failed to fetch payrolls';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Fetch payrolls error:', err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_BASE_URL}/create`, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setForm({ UserID: '', BasicSalary: '', HRA: '', DA: '', OtherAllowance: '' });
      fetchPayrolls();
    } catch (err) {
      let msg = 'Failed to add payroll';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Add payroll error:', err);
    }
  };

  const handleEdit = (payroll) => {
    setEditId(payroll.UserID);
    setForm({
      UserID: payroll.UserID,
      BasicSalary: payroll.BasicSalary,
      HRA: payroll.HRA,
      DA: payroll.DA,
      OtherAllowance: payroll.OtherAllowance,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_BASE_URL}/${editId}`, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEditId(null);
      setForm({ UserID: '', BasicSalary: '', HRA: '', DA: '', OtherAllowance: '' });
      fetchPayrolls();
    } catch (err) {
      alert('Failed to update payroll');
    }
  };

  // Delete payroll base handler
  const handleDeletePayroll = async (userId) => {
    if (!window.confirm("Are you sure you want to delete this payroll base record?")) return;
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`${API_BASE_URL}/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchPayrolls();
      alert("Payroll base deleted!");
    } catch (err) {
      alert('Delete failed: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="payroll-container">
      <h2 className="payroll-title">Payroll Base Management</h2>
      <form className="payroll-form" onSubmit={editId ? handleUpdate : handleAdd}>
        <input name="UserID" value={form.UserID} onChange={handleChange} placeholder="UserID" required className="payroll-input" />
        <input name="BasicSalary" value={form.BasicSalary} onChange={handleChange} placeholder="Basic Salary" required className="payroll-input" />
        <input name="HRA" value={form.HRA} onChange={handleChange} placeholder="HRA" className="payroll-input" />
        <input name="DA" value={form.DA} onChange={handleChange} placeholder="DA" className="payroll-input" />
        <input name="OtherAllowance" value={form.OtherAllowance} onChange={handleChange} placeholder="Other Allowance" className="payroll-input" />
        <button className="payroll-btn" type="submit">{editId ? 'Update' : 'Add'}</button>
        {editId && (
          <button className="payroll-btn" type="button" onClick={() => { setEditId(null); setForm({ UserID: '', BasicSalary: '', HRA: '', DA: '', OtherAllowance: '' }); }}>Cancel</button>
        )}
      </form>
      <table className="payroll-table">
        <thead>
          <tr>
            <th>UserID</th>
            <th>BasicSalary</th>
            <th>HRA</th>
            <th>DA</th>
            <th>OtherAllowance</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {payrolls.map((p) => (
            <tr key={p.UserID}>
              <td>{p.UserID}</td>
              <td>{p.BasicSalary}</td>
              <td>{p.HRA}</td>
              <td>{p.DA}</td>
              <td>{p.OtherAllowance}</td>
              <td>
                <button className="payroll-btn" onClick={() => handleEdit(p)}>Edit</button>
                <button className="payroll-btn" style={{ background: '#e53e3e', color: '#fff', marginLeft: 8 }} onClick={() => handleDeletePayroll(p.UserID)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PayrollPage;
