import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CompliancePage.css";

const API_BASE_URL = "http://localhost:3000/api/compliance";

const CompliancePage = () => {
  const [rules, setRules] = useState([]);
  const [form, setForm] = useState({
    Name: "",
    Value: "",
    Description: "",
    EffectiveFrom: "",
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchRules();
  }, []);

  const fetchRules = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRules(res.data);
    } catch (err) {
      alert("Failed to fetch compliance rules");
    }
  };

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post(API_BASE_URL, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setForm({ Name: "", Value: "", Description: "", EffectiveFrom: "" });
      fetchRules();
    } catch (err) {
      alert("Failed to add compliance rule");
    }
  };

  const handleEdit = (rule) => {
    setEditId(rule.RuleID);
    setForm({
      Name: rule.Name,
      Value: rule.Value,
      Description: rule.Description,
      EffectiveFrom: rule.EffectiveFrom ? rule.EffectiveFrom.slice(0, 10) : "",
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.put(`${API_BASE_URL}/${editId}`, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEditId(null);
      setForm({ Name: "", Value: "", Description: "", EffectiveFrom: "" });
      fetchRules();
    } catch (err) {
      alert("Failed to update compliance rule");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this compliance rule?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_BASE_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchRules();
    } catch (err) {
      alert("Failed to delete compliance rule");
    }
  };

  return (
    <div className="compliance-container">
      <h2 className="compliance-title">Compliance Rules Management</h2>
      <form className="compliance-form" onSubmit={editId ? handleUpdate : handleAdd}>
        <input
          name="Name"
          value={form.Name}
          onChange={handleChange}
          placeholder="Rule Name (PF)"
          required
          className="compliance-input"
        />
        <input
          name="Value"
          type="number"
          step="0.0001"
          value={form.Value}
          onChange={handleChange}
          placeholder="Value (e.g. 0.12 or 500)"
          required
          className="compliance-input"
        />
        <input
          name="Description"
          value={form.Description}
          onChange={handleChange}
          placeholder="Description"
          className="compliance-input"
        />
        <input
          name="EffectiveFrom"
          type="date"
          value={form.EffectiveFrom}
          onChange={handleChange}
          required
          className="compliance-input"
        />
        <button className="compliance-btn" type="submit">
          {editId ? "Update" : "Add"}
        </button>
        {editId && (
          <button
            className="compliance-btn"
            type="button"
            onClick={() => {
              setEditId(null);
              setForm({ Name: "", Value: "", Description: "", EffectiveFrom: "" });
            }}
          >
            Cancel
          </button>
        )}
      </form>
      <div className="compliance-table-wrapper">
        <table className="compliance-table">
          <thead>
            <tr>
              <th>Rule Name</th>
              <th>Value</th>
              <th>Description</th>
              <th>Effective From</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {rules.map((rule) => (
              <tr key={rule.RuleID}>
                <td>{rule.Name}</td>
                <td>{rule.Value}</td>
                <td>{rule.Description}</td>
                <td>{rule.EffectiveFrom ? rule.EffectiveFrom.slice(0, 10) : ""}</td>
                <td style={{ display: "flex", gap: "8px" }}>
                  <button className="compliance-btn" onClick={() => handleEdit(rule)}>Edit</button>
                  <button className="compliance-btn" style={{ marginLeft: "8px" }} onClick={() => handleDelete(rule.RuleID)}>Delete</button>
                </td>
              </tr>
            ))}
            {rules.length === 0 && (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", color: "#888" }}>
                  No compliance rules found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CompliancePage;
