import React from "react";

const LeavesTable = ({ leaves, loading, onEdit, onDelete }) => {
  if (loading) return <div>Loading...</div>;

  return (
    <div className="leaves-table-wrapper">
      <table className="leaves-table">
        <thead>
          <tr>
            <th>UserID</th>
            <th>LeaveType</th>
            <th>LeaveDays</th>
            <th>MonthYear</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {leaves.length === 0 ? (
            <tr><td colSpan={5} style={{ textAlign: "center" }}>No leave records found.</td></tr>
          ) : (
            leaves.map((leave) => (
              <tr key={leave.LeaveID || leave.id}>
                <td>{leave.UserID}</td>
                <td>{leave.LeaveType}</td>
                <td>{leave.LeaveDays}</td>
                <td>{leave.MonthYear}</td>
                <td>
                  <button className="leaves-btn" onClick={() => onEdit(leave)}>Edit</button>
                  <button className="leaves-btn" onClick={() => onDelete(leave.LeaveID || leave.id)}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default LeavesTable;
