import React, { useState } from "react";

const PayslipsForm = ({ onInsert }) => {
  const [form, setForm] = useState({ UserID: "", MonthYear: "" });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await onInsert(form.UserID, form.MonthYear);
      setForm({ UserID: "", MonthYear: "" });
      alert("Payslip inserted for new user!");
    } catch (err) {
      alert("Insert failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      className="payslips-insert-form"
      onSubmit={handleSubmit}
      style={{
        marginBottom: 24,
        display: "flex",
        gap: 12,
        alignItems: "center",
        flexWrap: "wrap",
      }}
    >
      <input
        name="UserID"
        value={form.UserID}
        onChange={handleChange}
        placeholder="User ID"
        required
        style={{ minWidth: 100 }}
      />
      <input
        name="MonthYear"
        value={form.MonthYear}
        onChange={handleChange}
        placeholder="MonthYear (e.g. Oct-2025)"
        required
        pattern="^[A-Za-z]{3}-\d{4}$"
        style={{ minWidth: 140 }}
      />
      <button type="submit" className="payslips-download-btn" disabled={loading}>
        {loading ? "Inserting..." : "Insert Payslip"}
      </button>
    </form>
  );
};

export default PayslipsForm;
