import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './PayrollPage.css';

const API_BASE_URL = 'http://localhost:3000/api/payroll-base';

const PayrollPage = () => {
  const [payrolls, setPayrolls] = useState([]);
  const [form, setForm] = useState({
    UserID: '',
    BasicSalary: '',
    HRA: '',
    DA: '',
    OtherAllowance: '',
  });
  const [editId, setEditId] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchPayrolls();
  }, []);

  const fetchPayrolls = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPayrolls(res.data);
    } catch (err) {
      let msg = 'Failed to fetch payrolls';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Fetch payrolls error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_BASE_URL}/create`, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setForm({ UserID: '', BasicSalary: '', HRA: '', DA: '', OtherAllowance: '' });
      fetchPayrolls();
    } catch (err) {
      let msg = 'Failed to add payroll';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Add payroll error:', err);
    }
  };

  const handleEdit = (payroll) => {
    setEditId(payroll.UserID);
    setForm({
      UserID: payroll.UserID,
      BasicSalary: payroll.BasicSalary,
      HRA: payroll.HRA,
      DA: payroll.DA,
      OtherAllowance: payroll.OtherAllowance,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_BASE_URL}/${editId}`, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEditId(null);
      setForm({ UserID: '', BasicSalary: '', HRA: '', DA: '', OtherAllowance: '' });
      fetchPayrolls();
    } catch (err) {
      alert('Failed to update payroll');
    }
  };

  const handleCancel = () => {
    setEditId(null);
    setForm({ UserID: '', BasicSalary: '', HRA: '', DA: '', OtherAllowance: '' });
  };

  const totalEmployees = payrolls.length;
  const totalBasicSalary = payrolls.reduce((sum, p) => sum + (parseFloat(p.BasicSalary) || 0), 0);
  const avgSalary = totalEmployees > 0 ? totalBasicSalary / totalEmployees : 0;

  return (
    <div className="payroll-container">
      <div className="payroll-header">
        <h1 className="payroll-title">Payroll Management</h1>
        <p className="payroll-subtitle">
          Manage employee salary structures and payroll information
        </p>
      </div>

      <div className="payroll-stats">
        <div className="payroll-stat-card">
          <div className="payroll-stat-icon">
            👥
          </div>
          <div className="payroll-stat-number">{totalEmployees}</div>
          <div className="payroll-stat-label">Total Employees</div>
        </div>
        <div className="payroll-stat-card">
          <div className="payroll-stat-icon">
            💰
          </div>
          <div className="payroll-stat-number">₹{totalBasicSalary.toLocaleString()}</div>
          <div className="payroll-stat-label">Total Basic Salary</div>
        </div>
        <div className="payroll-stat-card">
          <div className="payroll-stat-icon">
            📈
          </div>
          <div className="payroll-stat-number">₹{avgSalary.toLocaleString('en-IN', { maximumFractionDigits: 0 })}</div>
          <div className="payroll-stat-label">Average Salary</div>
        </div>
      </div>

      <div className="payroll-form-card">
        <h2 className="payroll-form-title">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12 4.5v15m7.5-7.5h-15" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          {editId ? 'Update Employee Payroll' : 'Add New Employee Payroll'}
        </h2>

        <form className="payroll-form" onSubmit={editId ? handleUpdate : handleAdd}>
          <div className="payroll-form-group">
            <label className="payroll-label">User ID</label>
            <input
              name="UserID"
              value={form.UserID}
              onChange={handleChange}
              placeholder="Enter User ID"
              required
              className="payroll-input"
              disabled={editId} // Disable UserID when editing
            />
          </div>

          <div className="payroll-form-group">
            <label className="payroll-label">Basic Salary</label>
            <input
              name="BasicSalary"
              type="number"
              value={form.BasicSalary}
              onChange={handleChange}
              placeholder="Enter Basic Salary"
              required
              className="payroll-input"
            />
          </div>

          <div className="payroll-form-group">
            <label className="payroll-label">HRA</label>
            <input
              name="HRA"
              type="number"
              value={form.HRA}
              onChange={handleChange}
              placeholder="Enter HRA amount"
              className="payroll-input"
            />
          </div>

          <div className="payroll-form-group">
            <label className="payroll-label">DA</label>
            <input
              name="DA"
              type="number"
              value={form.DA}
              onChange={handleChange}
              placeholder="Enter DA amount"
              className="payroll-input"
            />
          </div>

          <div className="payroll-form-group">
            <label className="payroll-label">Other Allowance</label>
            <input
              name="OtherAllowance"
              type="number"
              value={form.OtherAllowance}
              onChange={handleChange}
              placeholder="Enter Other Allowance"
              className="payroll-input"
            />
          </div>

          <div className="payroll-form-actions">
            <button className="payroll-btn" type="submit">
              {editId ? 'Update Payroll' : 'Add Payroll'}
            </button>
            {editId && (
              <button 
                className="payroll-btn payroll-btn-secondary" 
                type="button" 
                onClick={handleCancel}
              >
                Cancel
              </button>
            )}
          </div>
        </form>
      </div>

      <div className="payroll-table-card">
        <div className="payroll-table-header">
          <h3 className="payroll-table-title">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M9 17H7A2 2 0 0 1 5 15V7A2 2 0 0 1 7 5h2m0 12v-2a2 2 0 0 1 2-2h2m-4 4a2 2 0 0 0 2 2h2a2 2 0 0 0 2-2v-2h-6v2z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
            Payroll Records
          </h3>
        </div>

        <div className="payroll-table-wrapper">
          {loading ? (
            <div className="payroll-no-data">
              Loading payroll data...
            </div>
          ) : (
            <table className="payroll-table">
              <thead>
                <tr>
                  <th>User ID</th>
                  <th>Basic Salary</th>
                  <th>HRA</th>
                  <th>DA</th>
                  <th>Other Allowance</th>
                  <th>Total</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {payrolls.length === 0 ? (
                  <tr>
                    <td colSpan="7" className="payroll-no-data">
                      No payroll records found. Add your first employee above.
                    </td>
                  </tr>
                ) : (
                  payrolls.map((p) => {
                    const total = (parseFloat(p.BasicSalary) || 0) + 
                                 (parseFloat(p.HRA) || 0) + 
                                 (parseFloat(p.DA) || 0) + 
                                 (parseFloat(p.OtherAllowance) || 0);
                    return (
                      <tr key={p.UserID}>
                        <td><strong>{p.UserID}</strong></td>
                        <td>₹{parseFloat(p.BasicSalary || 0).toLocaleString()}</td>
                        <td>₹{parseFloat(p.HRA || 0).toLocaleString()}</td>
                        <td>₹{parseFloat(p.DA || 0).toLocaleString()}</td>
                        <td>₹{parseFloat(p.OtherAllowance || 0).toLocaleString()}</td>
                        <td><strong>₹{total.toLocaleString()}</strong></td>
                        <td>
                          <button 
                            className="payroll-edit-btn" 
                            onClick={() => handleEdit(p)}
                          >
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                              <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                              <path d="m18.5 2.5 3 3L12 15l-4 1 1-4L18.5 2.5z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                            </svg>
                            Edit
                          </button>
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default PayrollPage;
