// PayrollRunPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './PayrollRunPage.css';

const API_BASE_URL = 'http://localhost:3000/api/payroll-run';

const PayrollRunPage = () => {
  const [runs, setRuns] = useState([]);
  useEffect(() => {
    fetchRuns();
  }, []);

  const fetchRuns = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRuns(res.data);
    } catch (err) {
      alert('Failed to fetch payroll runs');
    }
  };

  return (
    <div className="payrollrun-container">
      <h2 className="payrollrun-title">Payroll Run Records</h2>
      <div className="payrollrun-table-wrapper">
        <table className="payrollrun-table">
          <thead>
            <tr>
              <th>PayrollRunID</th>
              <th>Month/Year</th>
              <th>Net Salary</th>
              <th>PF</th>
              <th>ESI</th>
              <th>TDS</th>
              <th>Actions</th>
            </tr>
          </thead>
        </table>
        <div className="payrollrun-table-body-scroll">
          <table className="payrollrun-table">
            <tbody>
              {runs.length === 0 ? (
                <tr><td colSpan={7} style={{ textAlign: 'center' }}>No payroll runs found.</td></tr>
              ) : (
                runs.map((run) => (
                  <tr key={run.PayrollRunID}>
                    <td>{run.PayrollRunID}</td>
                    <td>{run.MonthYear}</td>
                    <td>{run.NetSalary}</td>
                    <td>{run.PF}</td>
                    <td>{run.ESI}</td>
                    <td>{run.TDS}</td>
                    <td>
                      <span className="payrollrun-action-btns">
                        <button className="payrollrun-btn">View</button>
                      </span>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default PayrollRunPage;
