import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './PayrollPage.css';

const API_BASE_URL = 'http://localhost:3000/api/payroll';

const PayrollPage = () => {
  const [payrolls, setPayrolls] = useState([]);
  const [form, setForm] = useState({
    employee: '',
    month: '',
    amount: '',
    status: 'Pending',
  });
  const [editId, setEditId] = useState(null);
  const [loading, setLoading] = useState(false);

  // Fetch payrolls
  useEffect(() => {
    fetchPayrolls();
  }, []);

  const fetchPayrolls = async () => {
    setLoading(true);
    try {
      const res = await axios.get(API_BASE_URL);
      setPayrolls(res.data);
    } catch (err) {
      alert('Failed to fetch payrolls');
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await axios.post(API_BASE_URL, form);
      setForm({ employee: '', month: '', amount: '', status: 'Pending' });
      fetchPayrolls();
    } catch (err) {
      alert('Failed to add payroll');
    }
  };

  const handleEdit = (payroll) => {
    setEditId(payroll._id);
    setForm({
      employee: payroll.employee,
      month: payroll.month,
      amount: payroll.amount,
      status: payroll.status,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_BASE_URL}/${editId}`, form);
      setEditId(null);
      setForm({ employee: '', month: '', amount: '', status: 'Pending' });
      fetchPayrolls();
    } catch (err) {
      alert('Failed to update payroll');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this payroll?')) return;
    try {
      await axios.delete(`${API_BASE_URL}/${id}`);
      fetchPayrolls();
    } catch (err) {
      alert('Failed to delete payroll');
    }
  };

  return (
    <div className="payroll-container">
      <h2 className="payroll-title">Payroll Management</h2>
      <form className="payroll-form" onSubmit={editId ? handleUpdate : handleAdd} style={{ marginBottom: 24 }}>
        <input
          className="payroll-input"
          type="text"
          name="employee"
          placeholder="Employee Name"
          value={form.employee}
          onChange={handleChange}
          required
        />
        <input
          className="payroll-input"
          type="text"
          name="month"
          placeholder="Month (e.g. September 2025)"
          value={form.month}
          onChange={handleChange}
          required
        />
        <input
          className="payroll-input"
          type="number"
          name="amount"
          placeholder="Amount"
          value={form.amount}
          onChange={handleChange}
          required
        />
        <select
          className="payroll-input"
          name="status"
          value={form.status}
          onChange={handleChange}
        >
          <option value="Pending">Pending</option>
          <option value="Processed">Processed</option>
        </select>
        <button className="payroll-btn" type="submit" style={{ marginLeft: 12 }}>
          {editId ? 'Update Payroll' : 'Add Payroll'}
        </button>
        {editId && (
          <button className="payroll-btn" type="button" style={{ marginLeft: 12, background: '#e0e7ff', color: '#2563eb' }} onClick={() => { setEditId(null); setForm({ employee: '', month: '', amount: '', status: 'Pending' }); }}>
            Cancel
          </button>
        )}
      </form>
      <div className="payroll-table-wrapper">
        {loading ? (
          <div>Loading...</div>
        ) : (
          <table className="payroll-table">
            <thead>
              <tr>
                <th>Employee</th>
                <th>Month</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {payrolls.length === 0 ? (
                <tr><td colSpan={5} style={{ textAlign: 'center' }}>No payrolls found.</td></tr>
              ) : (
                payrolls.map((payroll) => (
                  <tr key={payroll._id}>
                    <td>{payroll.employee}</td>
                    <td>{payroll.month}</td>
                    <td>â‚¹{payroll.amount}</td>
                    <td>{payroll.status}</td>
                    <td>
                      <button className="payroll-btn" onClick={() => handleEdit(payroll)}>Edit</button>
                      <button className="payroll-btn" onClick={() => handleDelete(payroll._id)} style={{ marginLeft: 8, background: '#e0e7ff', color: '#2563eb' }}>Delete</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default PayrollPage;
