import React from "react";
import ReactDOM from "react-dom/client";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import AuthPage from './pages/auth/AuthPage';
import PayrollPage from './pages/payroll/PayrollPage';
import LeavesPage from './pages/leaves/LeavesPage';
import SalaryAdjustmentPage from './pages/salaryAdjustment/SalaryAdjustmentPage';
import PayrollRunPage from './pages/payrollRun/PayrollRunPage';
import ReportsPage from './pages/reports/ReportsPage';
import PayslipsPage from './pages/payslips/PayslipsPage';
import CompliancePage from './pages/compliance/CompliancePage';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';

function AppLayout() {
  const location = useLocation();
  const hideLayout = location.pathname === "/";
  return (
    <>
  {!hideLayout && <Navbar />}
  {!hideLayout && <Sidebar />}
      <Routes>
        <Route path="/" element={<AuthPage />} />
        <Route path="/payroll" element={<PayrollPage />} />
        <Route path="/leaves" element={<LeavesPage />} />
        <Route path="/salary-adjustment" element={<SalaryAdjustmentPage />} />
        <Route path="/payroll-run" element={<PayrollRunPage />} />
        <Route path="/reports" element={<ReportsPage />} />
        <Route path="/payslips" element={<PayslipsPage />} />
        <Route path="/compliance" element={<CompliancePage />} />
      </Routes>
    </>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <BrowserRouter>
    <AppLayout />
  </BrowserRouter>
);
