import React, { useEffect, useState } from "react";
import axios from "axios";
import "./SalaryAdjustmentPage.css";

const API_BASE_URL = "http://localhost:3000/api/adjustments";

const SalaryAdjustmentPage = () => {
  const [adjustments, setAdjustments] = useState([]);
  const [form, setForm] = useState({
    UserID: "",
    AdjustmentType: "",
    Amount: "",
    MonthYear: "",
    Remarks: "",
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchAdjustments();
  }, []);

  const fetchAdjustments = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAdjustments(res.data);
    } catch (err) {
      alert("Failed to fetch salary adjustments");
    }
  };

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post(API_BASE_URL, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setForm({
        UserID: "",
        AdjustmentType: "",
        Amount: "",
        MonthYear: "",
        Remarks: "",
      });
      fetchAdjustments();
    } catch (err) {
      alert("Failed to add adjustment");
    }
  };

  const handleEdit = (adj) => {
    setEditId(adj.AdjustmentID || adj._id);
    setForm({
      UserID: adj.UserID,
      AdjustmentType: adj.AdjustmentType,
      Amount: adj.Amount,
      MonthYear: adj.MonthYear,
      Remarks: adj.Remarks,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.put(`${API_BASE_URL}/${editId}`, form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEditId(null);
      setForm({
        UserID: "",
        AdjustmentType: "",
        Amount: "",
        MonthYear: "",
        Remarks: "",
      });
      fetchAdjustments();
    } catch (err) {
      alert("Failed to update adjustment");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this adjustment?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_BASE_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchAdjustments();
    } catch (err) {
      alert("Failed to delete adjustment");
    }
  };

  return (
    <div className="salary-adjustment-container">
      <h2 className="salary-adjustment-title">Salary Adjustments</h2>
      <form
        className="salary-adjustment-form"
        onSubmit={editId ? handleUpdate : handleAdd}
      >
        <input
          name="UserID"
          value={form.UserID}
          onChange={handleChange}
          placeholder="User ID"
          required
          className="salary-adjustment-input"
        />
        <select
          name="AdjustmentType"
          value={form.AdjustmentType}
          onChange={handleChange}
          required
          className="salary-adjustment-input"
        >
          <option value="">Type</option>
          <option value="Bonus">Bonus</option>
          <option value="Deduction">Deduction</option>
        </select>
        <input
          name="Amount"
          type="number"
          value={form.Amount}
          onChange={handleChange}
          placeholder="Amount"
          required
          className="salary-adjustment-input"
        />
        <input
          name="MonthYear"
          type="text"
          value={form.MonthYear}
          onChange={handleChange}
          placeholder="MonthYear (e.g. Sep-2025)"
          required
          className="salary-adjustment-input"
        />
        <input
          name="Remarks"
          value={form.Remarks}
          onChange={handleChange}
          placeholder="Remarks"
          className="salary-adjustment-input"
        />
        <button className="salary-adjustment-btn" type="submit">
          {editId ? "Update" : "Add"}
        </button>
        {editId && (
          <button
            className="salary-adjustment-btn"
            type="button"
            onClick={() => {
              setEditId(null);
              setForm({
                UserID: "",
                AdjustmentType: "",
                Amount: "",
                MonthYear: "",
                Remarks: "",
              });
            }}
          >
            Cancel
          </button>
        )}
      </form>
      <div className="salary-adjustment-table-wrapper">
        <table className="salary-adjustment-table">
          <thead>
            <tr>
              <th>User ID</th>
              <th>Type</th>
              <th>Amount</th>
              <th>Month/Year</th>
              <th>Remarks</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {adjustments.map((adj) => (
              <tr key={adj.AdjustmentID || adj._id}>
                <td>{adj.UserID}</td>
                <td>{adj.AdjustmentType}</td>
                <td>{adj.Amount}</td>
                <td>{adj.MonthYear}</td>
                <td>{adj.Remarks}</td>
                <td>
                  <div style={{ display: "flex", gap: "8px" }}>
                    <button
                      className="salary-adjustment-btn"
                      onClick={() => handleEdit(adj)}
                    >
                      Edit
                    </button>
                    <button
                      className="salary-adjustment-btn"
                      onClick={() => handleDelete(adj.AdjustmentID || adj._id)}
                    >
                      Delete
                    </button>
                  </div>
                </td>
              </tr>
            ))}
            {adjustments.length === 0 && (
              <tr>
                <td colSpan="6" style={{ textAlign: "center", color: "#888" }}>
                  No adjustments found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SalaryAdjustmentPage;
