// This file contains the PayslipsPage component to show payslips and allow download.
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./PayslipsPage.css";

const API_BASE_URL = "http://localhost:3000/api/payslips/my";

const PayslipsPage = () => {
	const [payslips, setPayslips] = useState([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState("");
	const [form, setForm] = useState({ UserID: "", MonthYear: "" });
	const [insertLoading, setInsertLoading] = useState(false);
	const handleFormChange = (e) => {
		setForm({ ...form, [e.target.name]: e.target.value });
	};

		const handleInsertPayslip = async (e) => {
			e.preventDefault();
			setInsertLoading(true);
			try {
				const token = localStorage.getItem("token");
				// 1. Find payroll run for user/month
				const prRes = await axios.get("http://localhost:3000/api/payroll-runs", {
					headers: { Authorization: `Bearer ${token}` }
				});
				const runs = Array.isArray(prRes.data) ? prRes.data : [];
				const run = runs.find(r => String(r.UserID) === String(form.UserID) && r.MonthYear === form.MonthYear);
				if (!run) {
					alert("No payroll run found for this user and month. Please create payroll run first.");
					setInsertLoading(false);
					return;
				}
				// 2. Call payslip generate endpoint
				await axios.post("http://localhost:3000/api/payslips/generate", { payrollRunId: run.PayrollRunID }, {
					headers: { Authorization: `Bearer ${token}` }
				});
				setForm({ UserID: "", MonthYear: "" });
				fetchPayslips();
				alert("Payslip inserted for new user!");
			} catch (err) {
				alert("Insert failed: " + (err.response?.data?.message || err.message));
			}
			setInsertLoading(false);
		};

	useEffect(() => {
		fetchPayslips();
	}, []);

	const fetchPayslips = async () => {
		setLoading(true);
		setError("");
		try {
			const token = localStorage.getItem("token");
			const res = await axios.get(API_BASE_URL, {
				headers: { Authorization: `Bearer ${token}` },
			});
			setPayslips(res.data);
		} catch (err) {
			setError(err.response?.data?.message || err.message || "Failed to fetch payslips");
		} finally {
			setLoading(false);
		}
	};

	const handleDownload = async (slip) => {
		const token = localStorage.getItem("token");
		try {
			const response = await axios.get(
				`http://localhost:3000${slip.downloadUrl}`,
				{
					headers: { Authorization: `Bearer ${token}` },
					responseType: "blob",
				}
			);
			// Use MonthYear and UserID for filename clarity
			const filename = `payslip_${slip.UserID}_${slip.MonthYear}.pdf`;
			const url = window.URL.createObjectURL(new Blob([response.data]));
			const link = document.createElement("a");
			link.href = url;
			link.setAttribute("download", filename);
			document.body.appendChild(link);
			link.click();
			document.body.removeChild(link);
			window.URL.revokeObjectURL(url);
		} catch (err) {
			alert("Download failed: " + (err.response?.data?.message || err.message));
		}
	};

		return (
			<div className="payslips-container">
				<h2 className="payslips-title">Payslips</h2>
				{/* Insert Payslip for New User */}
				<form className="payslips-insert-form" onSubmit={handleInsertPayslip} style={{ marginBottom: 24, display: 'flex', gap: 12, alignItems: 'center', flexWrap: 'wrap' }}>
					<input
						name="UserID"
						value={form.UserID}
						onChange={handleFormChange}
						placeholder="User ID"
						required
						style={{ minWidth: 100 }}
					/>
					<input
						name="MonthYear"
						value={form.MonthYear}
						onChange={handleFormChange}
						placeholder="MonthYear (e.g. Oct-2025)"
						required
						pattern="^[A-Za-z]{3}-\d{4}$"
						style={{ minWidth: 140 }}
					/>
					<button type="submit" className="payslips-download-btn" disabled={insertLoading}>
						{insertLoading ? "Inserting..." : "Insert Payslip"}
					</button>
				</form>
				{loading ? (
					<div className="payslips-loading">Loading...</div>
				) : error ? (
					<div className="payslips-error">{error}</div>
				) : !payslips.length ? (
					<div className="payslips-empty">No payslips found.</div>
				) : (
					<table className="payslips-table">
						<thead>
							<tr>
								<th>Payslip ID</th>
								<th>User ID</th>
								<th>Month/Year</th>
								<th>Net Salary</th>
								<th>Download</th>
							</tr>
						</thead>
						<tbody>
							{payslips.map((slip) => (
								<tr key={slip.PayslipID}>
									<td>{slip.PayslipID}</td>
									<td>{slip.UserID}</td>
									<td>{slip.MonthYear}</td>
									<td>{slip.NetSalary}</td>
													<td style={{ display: 'flex', gap: '8px' }}>
														{slip.downloadUrl ? (
															<button
																className="payslips-download-btn"
																onClick={() => handleDownload(slip)}
															>
																Download
															</button>
														) : (
															<button
																className="payslips-download-btn"
																disabled
																title="PDF not available for this payslip"
															>
																Not Available
															</button>
														)}
														<button
															className="payslips-download-btn"
															style={{ background: '#e53e3e', color: '#fff' }}
															onClick={async () => {
																if (!window.confirm('Are you sure you want to delete this payslip?')) return;
																const token = localStorage.getItem('token');
																try {
																	await axios.delete(`http://localhost:3000/api/payslips/my/${slip.PayslipID}`, {
																		headers: { Authorization: `Bearer ${token}` }
																	});
																	fetchPayslips();
																	alert('Payslip deleted!');
																} catch (err) {
																	alert('Delete failed: ' + (err.response?.data?.message || err.message));
																}
															}}
														>
															Delete
														</button>
													</td>
								</tr>
							))}
						</tbody>
					</table>
				)}
			</div>
		);
};

export default PayslipsPage;
