import React from 'react';
import { NavLink } from 'react-router-dom';
import { 
  FaUserPlus,
  FaMoneyBillWave, 
  FaPlay, 
  FaCalendarAlt, 
  FaClipboardCheck, 
  FaChartBar, 
  FaAdjust, 
  FaFileInvoice 
} from 'react-icons/fa';
import './Sidebar.css';

const Sidebar = ({role, name, userId}) => {
  // If role, name, or userId are not provided as props, try to get from localStorage
  let userRaw = localStorage.getItem("user");
  let user = {};
  if (userRaw && userRaw !== "undefined") {
    user = JSON.parse(userRaw);
  }
  const safeRole = role || user?.Role || "";
  const safeName = name || user?.Name || "";
  const safeUserId = userId || user?.UserID || "";
  return (
    <aside className="sidebar">
      <nav className="sidebar-nav">
        {safeRole !== 'Employee' && (
          <>
            <NavLink to="/AddUser" className="sidebar-link">
              <FaUserPlus className="sidebar-icon" />
              <span>Add User</span>
            </NavLink>
            <NavLink to="/payroll" className="sidebar-link">
              <FaMoneyBillWave className="sidebar-icon" />
              <span>Payroll</span>
            </NavLink>
            <NavLink to="/leaves" className="sidebar-link">
              <FaCalendarAlt className="sidebar-icon" />
              <span>Leaves</span>
            </NavLink>
            <NavLink to="/salaryAdjustment" className="sidebar-link">
              <FaAdjust className="sidebar-icon" />
              <span>Salary Adjustment</span>
            </NavLink>
            <NavLink to="/compliance" className="sidebar-link">
              <FaClipboardCheck className="sidebar-icon" />
              <span>Compliance</span>
            </NavLink>
            <NavLink to="/reports" className="sidebar-link">
              <FaChartBar className="sidebar-icon" />
              <span>Reports</span>
            </NavLink>
          </>
        )}
        <NavLink to="/payrollRun" className="sidebar-link">
          <FaPlay className="sidebar-icon" />
          <span>Payroll Run</span>
        </NavLink>
        <NavLink to="/payslips" className="sidebar-link">
          <FaFileInvoice className="sidebar-icon" />
          <span>Payslips</span>
        </NavLink>
      </nav>
    </aside>
  );
};

export default Sidebar;
