import React from "react";

const LeavesForm = ({ form, setForm, editId, onAdd, onUpdate, onCancel }) => {
  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    editId ? onUpdate(editId, form) : onAdd(form);
    setForm({ UserID: "", LeaveType: "", LeaveDays: "", MonthYear: "" });
  };

  return (
    <form className="leaves-form" onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
      <input className="leaves-input" name="UserID" value={form.UserID} onChange={handleChange} placeholder="UserID" required />
      <input className="leaves-input" name="LeaveType" value={form.LeaveType} onChange={handleChange} placeholder="Leave Type" required />
      <input className="leaves-input" type="number" name="LeaveDays" value={form.LeaveDays} onChange={handleChange} placeholder="Leave Days" required />
      <input className="leaves-input" name="MonthYear" value={form.MonthYear} onChange={handleChange} placeholder="MonthYear (e.g. Sep-2025)" required />

      <button className="leaves-btn" type="submit">{editId ? "Update Leave" : "Add Leave"}</button>
      {editId && (
        <button className="leaves-btn" type="button" onClick={onCancel} style={{ marginLeft: 12 }}>
          Cancel
        </button>
      )}
    </form>
  );
};

export default LeavesForm;
