import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReportsPage.css";

const API_BASE_URL = "http://localhost:3000/api/reports";

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReports(res.data);
    } catch (err) {
      setError(
        err.response?.data?.message || err.message || "Failed to fetch reports"
      );
    } finally {
      setLoading(false);
    }
  };

  const filteredReports = reports.filter(
    (report) =>
      report.id !== "payslip" &&
      report.id !== "compliance" &&
      report.id !== "payroll_summary"
  );

  return (
    <div className="reports-container">
      <div className="reports-header">
        <h1 className="reports-title">Reports Dashboard</h1>
        <p className="reports-subtitle">
          Download and manage your payroll reports
        </p>
      </div>

      {loading ? (
        <div className="reports-content">
          <div className="reports-loading">
            <div className="reports-loading-icon"></div>
            <span>Loading reports...</span>
          </div>
        </div>
      ) : error ? (
        <div className="reports-content">
          <div className="reports-error">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
              <line x1="15" y1="9" x2="9" y2="15" stroke="currentColor" strokeWidth="2"/>
              <line x1="9" y1="9" x2="15" y2="15" stroke="currentColor" strokeWidth="2"/>
            </svg>
            {error}
          </div>
        </div>
      ) : (
        <div className="reports-content">
          <div className="reports-stats">
            <div className="reports-stat">
              <div className="reports-stat-number">{filteredReports.length}</div>
              <div className="reports-stat-label">Available Reports</div>
            </div>
            <div className="reports-stat">
              <div className="reports-stat-number">ðŸ“Š</div>
              <div className="reports-stat-label">Data Analytics</div>
            </div>
            <div className="reports-stat">
              <div className="reports-stat-number">âš¡</div>
              <div className="reports-stat-label">Quick Download</div>
            </div>
          </div>

          <div className="reports-table-wrapper">
            <table className="reports-table">
              <thead>
                <tr>
                  <th>Report ID</th>
                  <th>Report Type</th>
                  <th>Description</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredReports.length === 0 ? (
                  <tr>
                    <td colSpan="4" className="reports-no-data">
                      No reports available at the moment.
                    </td>
                  </tr>
                ) : (
                  filteredReports.map((report) => (
                    <tr key={report.id}>
                      <td>
                        <strong>{report.id}</strong>
                      </td>
                      <td>{report.name}</td>
                      <td>{report.description}</td>
                      <td>
                        {report.id === "leaves_csv" ? (
                          <button
                            className="reports-download-btn"
                            onClick={async () => {
                              const token = localStorage.getItem("token");
                              try {
                                const leaveRes = await axios.get("http://localhost:3000/api/leaves", {
                                  headers: { Authorization: `Bearer ${token}` }
                                });
                                const leaves = leaveRes.data;
                                const Papa = require('papaparse');
                                const headers = ["LeaveID","UserID","LeaveType","LeaveDays","MonthYear","CreatedAt"];
                                const csvContent = Papa.unparse(leaves, {
                                  quotes: true,
                                  header: true,
                                  columns: headers
                                });
                                const blob = new Blob([csvContent], { type: "text/csv" });
                                const blobUrl = window.URL.createObjectURL(blob);
                                const link = document.createElement("a");
                                link.href = blobUrl;
                                link.setAttribute("download", "leaves.csv");
                                document.body.appendChild(link);
                                link.click();
                                link.remove();
                              } catch (err) {
                                alert("Download failed: " + (err.response?.data?.message || err.message));
                              }
                            }}
                          >
                            Download CSV
                          </button>
                        ) : report.id === "salary_adjustment_csv" ? (
                          <button
                            className="reports-download-btn"
                            onClick={async () => {
                              const token = localStorage.getItem("token");
                              try {
                                const res = await axios.get("http://localhost:3000/api/salary-adjustments", {
                                  headers: { Authorization: `Bearer ${token}` }
                                });
                                if (res.data && typeof res.data === 'object' && res.data.message) {
                                  alert("Download failed: " + res.data.message);
                                  return;
                                }
                                const adjustments = Array.isArray(res.data) ? res.data : [];
                                if (adjustments.length === 0) {
                                  alert("No salary adjustment data available.");
                                  return;
                                }
                                const Papa = require('papaparse');
                                const headers = ["AdjustmentID","UserID","Amount","Reason","MonthYear","CreatedAt"];
                                const formatted = adjustments.map(a => ({
                                  AdjustmentID: a.AdjustmentID || a.id || '',
                                  UserID: a.UserID || a.userId || '',
                                  Amount: a.Amount || a.amount || '',
                                  Reason: a.Reason || a.reason || '',
                                  MonthYear: a.MonthYear || a.monthYear || '',
                                  CreatedAt: a.CreatedAt || a.createdAt || ''
                                }));
                                const csvContent = Papa.unparse(formatted, {
                                  quotes: true,
                                  header: true,
                                  columns: headers
                                });
                                const blob = new Blob([csvContent], { type: "text/csv" });
                                const blobUrl = window.URL.createObjectURL(blob);
                                const link = document.createElement("a");
                                link.href = blobUrl;
                                link.setAttribute("download", "salary_adjustments.csv");
                                document.body.appendChild(link);
                                link.click();
                                link.remove();
                              } catch (err) {
                                alert("Download failed: " + (err.response?.data?.message || err.message));
                              }
                            }}
                          >
                            Download CSV
                          </button>
                        ) : report.endpoint ? (
                          <button
                            className="reports-download-btn"
                            onClick={async () => {
                              const token = localStorage.getItem("token");
                              try {
                                const url =
                                  report.id === "compliance_csv"
                                    ? `http://localhost:3000${report.endpoint}?month=Sep-2025`
                                    : `http://localhost:3000${report.endpoint}`;
                                const response = await axios.get(url, {
                                  headers: { Authorization: `Bearer ${token}` },
                                  responseType: "blob",
                                });
                                const fileName =
                                  report.id === "compliance_csv"
                                    ? `compliance_Sep-2025.csv`
                                    : `${report.id}.csv`;
                                const blobUrl = window.URL.createObjectURL(
                                  new Blob([response.data])
                                );
                                const link = document.createElement("a");
                                link.href = blobUrl;
                                link.setAttribute("download", fileName);
                                document.body.appendChild(link);
                                link.click();
                                link.remove();
                              } catch (err) {
                                alert(
                                  "Download failed: " +
                                    (err.response?.data?.message || err.message)
                                );
                              }
                            }}
                          >
                            Download CSV
                          </button>
                        ) : (
                          <span style={{ color: "#9ca3af", fontStyle: "italic" }}>N/A</span>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReportsPage;
