import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './LeavesPage.css';

const API_BASE_URL = 'http://localhost:3000/api/leaves';

const LeavesPage = () => {
  const [leaves, setLeaves] = useState([]);
  const [form, setForm] = useState({
    UserID: '',
    LeaveType: '',
    LeaveDays: '',
    MonthYear: '',
  });
  const [editId, setEditId] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchLeaves();
  }, []);

  const fetchLeaves = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setLeaves(res.data);
    } catch (err) {
      let msg = 'Failed to fetch leaves';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Fetch leaves error:', err);
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${API_BASE_URL}/createLeave`, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setForm({ UserID: '', LeaveType: '', LeaveDays: '', MonthYear: '' });
      fetchLeaves();
    } catch (err) {
      let msg = 'Failed to add leave';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Add leave error:', err);
    }
  };

  const handleEdit = (leave) => {
    setEditId(leave._id);
    setForm({
      UserID: leave.UserID,
      LeaveType: leave.LeaveType,
      LeaveDays: leave.LeaveDays,
      MonthYear: leave.MonthYear,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_BASE_URL}/${editId}`, form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEditId(null);
      setForm({ UserID: '', LeaveType: '', LeaveDays: '', MonthYear: '' });
      fetchLeaves();
    } catch (err) {
      let msg = 'Failed to update leave';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Update leave error:', err);
    }
  };

  const handleDelete = async (LeaveID) => {
    if (!window.confirm('Delete this leave record?')) return;
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${API_BASE_URL}/${LeaveID}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchLeaves();
    } catch (err) {
      let msg = 'Failed to delete leave';
      if (err.response) msg += ': ' + (err.response.data?.error || JSON.stringify(err.response.data));
      else if (err.request) msg += ': No response from server.';
      else msg += ': ' + err.message;
      alert(msg);
      console.error('Delete leave error:', err);
    }
  };

  return (
    <div className="leaves-container">
      <h2 className="leaves-title">Leave Records</h2>
      <form className="leaves-form" onSubmit={editId ? handleUpdate : handleAdd} style={{ marginBottom: 24 }}>
        <input
          className="leaves-input"
          type="text"
          name="UserID"
          placeholder="UserID"
          value={form.UserID}
          onChange={handleChange}
          required
        />
        <input
          className="leaves-input"
          type="text"
          name="LeaveType"
          placeholder="Leave Type (e.g. Casual, Sick, LOP)"
          value={form.LeaveType}
          onChange={handleChange}
          required
        />
        <input
          className="leaves-input"
          type="number"
          name="LeaveDays"
          placeholder="Leave Days"
          value={form.LeaveDays}
          onChange={handleChange}
          required
        />
        <input
          className="leaves-input"
          type="text"
          name="MonthYear"
          placeholder="MonthYear (e.g. Sep-2025)"
          value={form.MonthYear}
          onChange={handleChange}
          required
        />
        <button className="leaves-btn" type="submit" style={{ marginLeft: 12 }}>
          {editId ? 'Update Leave' : 'Add Leave'}
        </button>
        {editId && (
          <button className="leaves-btn" type="button" style={{ marginLeft: 12, background: '#e0e7ff', color: '#2563eb' }} onClick={() => { setEditId(null); setForm({ UserID: '', LeaveType: '', LeaveDays: '', MonthYear: '' }); }}>
            Cancel
          </button>
        )}
      </form>
      <div className="leaves-table-wrapper">
        {loading ? (
          <div>Loading...</div>
        ) : (
          <table className="leaves-table">
            <thead>
              <tr>
                <th>UserID</th>
                <th>LeaveType</th>
                <th>LeaveDays</th>
                <th>MonthYear</th>
                <th>Actions</th>
              </tr>
            </thead>
          </table>
        )}
        {!loading && (
          <div className="leaves-table-body-scroll">
            <table className="leaves-table">
              <tbody>
                {leaves.length === 0 ? (
                  <tr><td colSpan={5} style={{ textAlign: 'center' }}>No leave records found.</td></tr>
                ) : (
                  leaves.map((leave) => (
                    <tr key={leave._id}>
                      <td>{leave.UserID}</td>
                      <td>{leave.LeaveType}</td>
                      <td>{leave.LeaveDays}</td>
                      <td>{leave.MonthYear}</td>
                      <td>
                        <span className="leaves-action-btns">
                          <button className="leaves-btn" onClick={() => handleEdit(leave)}>Edit</button>
                          <button className="leaves-btn" onClick={() => handleDelete(leave.LeaveID || leave.id)}>Delete</button>
                        </span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default LeavesPage;
