import React from "react";
import "./PayrollPage.css";

const PayrollForm = ({ form, editId, onChange, onSubmit, onCancel }) => {
  return (
    <form className="payroll-form" onSubmit={onSubmit}>
      <input
        name="UserID"
        value={form.UserID}
        onChange={onChange}
        placeholder="UserID"
        required
        className="payroll-input"
      />
      <input
        name="BasicSalary"
        value={form.BasicSalary}
        onChange={onChange}
        placeholder="Basic Salary"
        required
        className="payroll-input"
      />
      <input
        name="HRA"
        value={form.HRA}
        onChange={onChange}
        placeholder="HRA"
        className="payroll-input"
      />
      <input
        name="DA"
        value={form.DA}
        onChange={onChange}
        placeholder="DA"
        className="payroll-input"
      />
      <input
        name="OtherAllowance"
        value={form.OtherAllowance}
        onChange={onChange}
        placeholder="Other Allowance"
        className="payroll-input"
      />

      <button className="payroll-btn" type="submit">
        {editId ? "Update" : "Add"}
      </button>

      {editId && (
        <button
          className="payroll-btn"
          type="button"
          onClick={onCancel}
        >
          Cancel
        </button>
      )}
    </form>
  );
};

export default PayrollForm;
