import React from "react";

const ComplianceForm = ({ form, setForm, editId, onAdd, onUpdate, onCancel }) => {
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editId) onUpdate();
    else onAdd();
  };

  return (
    <form className="compliance-form" onSubmit={handleSubmit}>
      <input
        name="Name"
        value={form.Name}
        onChange={handleChange}
        placeholder="Rule Name (PF)"
        required
        className="compliance-input"
      />
      <input
        name="Value"
        type="number"
        step="0.0001"
        value={form.Value}
        onChange={handleChange}
        placeholder="Value (e.g. 0.12 or 500)"
        required
        className="compliance-input"
      />
      <input
        name="Description"
        value={form.Description}
        onChange={handleChange}
        placeholder="Description"
        className="compliance-input"
      />
      <input
        name="EffectiveFrom"
        type="date"
        value={form.EffectiveFrom}
        onChange={handleChange}
        required
        className="compliance-input"
      />
      <button className="compliance-btn" type="submit">
        {editId ? "Update" : "Add"}
      </button>
      {editId && (
        <button className="compliance-btn" type="button" onClick={onCancel}>
          Cancel
        </button>
      )}
    </form>
  );
};

export default ComplianceForm;
