import React from "react";
import { fetchLeaves, fetchSalaryAdjustments, downloadReport } from "./reportsService";
import Papa from "papaparse";

const ReportRow = ({ report }) => {
  const handleLeavesDownload = async () => {
    try {
      const leaves = await fetchLeaves();
      const headers = ["LeaveID","UserID","LeaveType","LeaveDays","MonthYear","CreatedAt"];
      const csvContent = Papa.unparse(leaves, { header: true, columns: headers });
      triggerDownload(csvContent, "leaves.csv");
    } catch (err) {
      alert("Download failed: " + err.message);
    }
  };

  const handleSalaryAdjustmentsDownload = async () => {
    try {
      const adjustments = await fetchSalaryAdjustments();
      if (!Array.isArray(adjustments) || adjustments.length === 0) {
        alert("No salary adjustment data available.");
        return;
      }
      const formatted = adjustments.map(a => ({
        AdjustmentID: a.AdjustmentID || "",
        UserID: a.UserID || "",
        Amount: a.Amount || "",
        Reason: a.Reason || "",
        MonthYear: a.MonthYear || "",
        CreatedAt: a.CreatedAt || ""
      }));
      const headers = ["AdjustmentID","UserID","Amount","Reason","MonthYear","CreatedAt"];
      const csvContent = Papa.unparse(formatted, { header: true, columns: headers });
      triggerDownload(csvContent, "salary_adjustments.csv");
    } catch (err) {
      alert("Download failed: " + err.message);
    }
  };

  const triggerDownload = (csvContent, fileName) => {
    const blob = new Blob([csvContent], { type: "text/csv" });
    const blobUrl = window.URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = blobUrl;
    link.setAttribute("download", fileName);
    document.body.appendChild(link);
    link.click();
    link.remove();
  };

  const handleGenericDownload = async () => {
    try {
      const fileName = report.id === "compliance_csv"
        ? `compliance_Sep-2025.csv`
        : `${report.id}.csv`;
      await downloadReport(report.endpoint, fileName);
    } catch (err) {
      alert("Download failed: " + err.message);
    }
  };

  return (
    <tr key={report.id}>
      <td>{report.id}</td>
      <td>{report.name}</td>
      <td>{report.description}</td>
      <td>
        {report.id === "leaves_csv" ? (
          <button className="reports-download-btn" onClick={handleLeavesDownload}>Download</button>
        ) : report.id === "salary_adjustment_csv" ? (
          <button className="reports-download-btn" onClick={handleSalaryAdjustmentsDownload}>Download</button>
        ) : report.endpoint ? (
          <button className="reports-download-btn" onClick={handleGenericDownload}>Download</button>
        ) : (
          "-"
        )}
      </td>
    </tr>
  );
};

export default ReportRow;
