import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReportsPage.css";

const API_BASE_URL = "http://localhost:3000/api/reports";

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReports(res.data);
    } catch (err) {
      setError(
        err.response?.data?.message || err.message || "Failed to fetch reports"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reports-container">
      <h2 className="reports-title">Reports</h2>
      {loading ? (
        <div className="reports-loading">Loading...</div>
      ) : error ? (
        <div className="reports-error">{error}</div>
      ) : (
        <table className="reports-table">
          <thead>
            <tr>
              <th>Report ID</th>
              <th>Type</th>
              <th>Month/Year</th>
              <th>Created At</th>
              <th>Download</th>
            </tr>
          </thead>
          <tbody>
            {reports.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", color: "#888" }}>
                  No reports found.
                </td>
              </tr>
            ) : (
              reports.map((report) => (
                <tr key={report.ReportID || report._id}>
                  <td>{report.ReportID || report._id}</td>
                  <td>{report.Type}</td>
                  <td>{report.MonthYear}</td>
                  <td>{report.CreatedAt}</td>
                  <td>
                    {report.FilePath ? (
                      <a
                        href={report.FilePath}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="reports-download-btn"
                      >
                        Download
                      </a>
                    ) : (
                      "-"
                    )}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ReportsPage;
